mapper.select:
SELECT

FROM

WHERE conversation_id = #{conversationId}
ORDER BY created_date DESC
LIMIT #{offset},#{limit}

SELECT
, COUNT (id) as id
FROM
( SELECT * FROM message
  WHERE
  from_id=#{userId} or to_id=#{userId}
  order by created_date desc
) tt
group by conversation_id order by created_date desc limit #{offset}, #{limit}
